// HTML 문자열 내에 Global Stylesheet; dangerouslySetInnerHTML을 이용
// pages/_document.js
import Document, { Head, Main, NextScript } from 'next/document';

export default class CustomDocument extends Document {
  static getInitialProps ({ renderPage }) {
    const {
      html,
      head,
      errorHtml,
      chunks
    } = renderPage();

    return { html, head, errorHtml, chunks };
  }

  render() {
    return (
      <html>
        <Head>
          <style dangerouslySetInnerHTML={{ __html: `
            @import url(https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css);
            * {
              box-sizing: border-box;
              user-select: none;
            }
            html, body {
              padding: 0;
              margin: 0;
            }
            .peeek-loading {
              overflow: hidden;
              position: absolute;
              top: 0;
              bottom: 0;
              left: 0;
              right: 0;
              height: 100%;
              width: 100%;
            }
            .peeek-loading span {
              position: absolute;
              left: 50%;
              top: 30%;
              transform: translate(-50%, -50%);
              font-size: 2em;
              text-align: center;
              font-family: Arial;
              color: white;
              animation: blinking 2s ease-in-out infinite;
            }
            @keyframes blinking {
              0%, 100% {
                opacity: 1;
              }
              40% {
                opacity: 0;
              }
            }
            .overlay {
              position: fixed;
              width: 100%;
              height: 100%;
              top: 0;
              left: 0;
              z-index: 10000;
            }
          `}} />
        </Head>
        <body>
          <Main />
          <NextScript />
        </body>
      </html>
    )
  }
}
