import Layout from "../components/Layout";
import Link from "next/link";
import fetch from "isomorphic-unfetch";

const ProfileLink = props => (
  <div>
    <Link href={`/p/[profile]`} as={`/p/${props.profile}`}>
      <a> go to {props.profile}'s profiles </a>
    </Link>
  </div>
);

const Index = props => (
  <Layout>
    <h1>Friends List {props.profiles[0]}</h1>
    {props.profiles.map((profile, index) => (
      <ProfileLink Key={index} profile={profile} />
    ))}
  </Layout>
);

Index.getInitialProps = async function () {
  const res = await fetch("https://api.themoviedb.org/3/movie/550?api_key=a83dc777ac0d1ebb7501248e01ab9f50");
  const data = await res.json();

    return{
        profile: data
    }
};

export default Index;
